import React, { useEffect, useState } from "react";
import useStyles from "./style";
import { useDispatch, useSelector } from "react-redux";

/* From Material UI */
import { Button, Paper, TextField, Typography } from "@material-ui/core";
import FileBase from "react-file-base64";
import { createPost, updatePost, deletePost } from "../../actions/posts";

const Form = ({ currentId, setCurrentId }) => {
  const [postData, setPostData] = useState({
    creator: "",
    title: "",
    tags: "",
    message: "",
    selectedFile: "",
  });

  const classes = useStyles();
  const dispatch = useDispatch();

  const { creator, title, tags, message } = postData;

  const post = useSelector((state) => {
    return currentId
      ? state.posts.find((p) => {
          return p._id === currentId;
        })
      : null;
  });

  useEffect(() => {
    if (post) setPostData(post);
  }, [post]);

  const onChange = (e) => {
    setPostData({
      ...postData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (currentId) {
      dispatch(updatePost(currentId, postData));
    } else {
      dispatch(createPost(postData));
    }

    clear();
  };

  const handleBase64 = ({ base64 }) => {
    setPostData({
      ...postData,
      selectedFile: base64,
    });
  };

  const deletePostHandler = (e) => {
    e.preventDefault();

    deletePost(post.id);
  };

  const clear = () => {
    setCurrentId(null);

    setPostData({
      creator: "",
      title: "",
      tags: "",
      message: "",
      selectedFile: "",
    });
  };

  return (
    <Paper className={classes.paper}>
      <form
        autoComplete="off"
        noValidate
        className={`${classes.root} ${classes.form}`}
        onSubmit={handleSubmit}
      >
        <Typography variant="h6">
          {currentId ? "Editing" : "Creating"} a Memory
        </Typography>
        {/* Creator */}
        <TextField
          name="creator"
          variant="outlined"
          label="Creator"
          fullWidth
          value={creator}
          onChange={onChange}
        />

        {/* Title */}
        <TextField
          name="title"
          variant="outlined"
          label="Title"
          fullWidth
          value={title}
          onChange={onChange}
        />

        {/* Message */}
        <TextField
          name="message"
          variant="outlined"
          label="Message"
          fullWidth
          value={message}
          onChange={onChange}
        />

        {/* Tags */}
        <TextField
          name="tags"
          variant="outlined"
          label="Tags"
          fullWidth
          value={tags}
          onChange={onChange}
        />

        <div className={classes.fileInput}>
          <FileBase type="file" multiple={false} onDone={handleBase64} />
        </div>

        <Button
          className={classes.buttonSubmit}
          variant="contained"
          color="primary"
          size="large"
          type="submit"
          fullWidth
        >
          Submit
        </Button>
        <Button
          className={classes.buttonSubmit}
          variant="contained"
          color="secondary"
          size="large"
          onClick={deletePostHandler}
          fullWidth
          onClick={deletePostHandler}
        >
          Delete
        </Button>
      </form>
    </Paper>
  );
};

export default Form;
