import * as api from '../api';

// Action Creator
export const getPosts = () => {
  async (dispatch) => {
    try {
      const { data } = api.fetchPosts()
      dispatch({
        type: 'FETCH_ALL'
      })
    } catch (error) {
      
    }
    
  };
};
