import React, { useState } from 'react'
import useStyles from './style';

/* From Material UI */
import { TextField, Button, Typography, Paper } from '@material-ui/core'
import FileBase from 'react-file-base64'

const Form = () => {
  const [postData, setPostData] = useState({
    creator: '',
    title: '',
    tags: '',
    message: '',
    selectedFile: '',
  })
  const classes = useStyles();

  const handleSubmit = (e) => {
    console.log('Form submited')
  }

  const clear = () => {

  }

  const handlChange = (e) => {
    setPostData({
      [e.target.name]: e.target.value
    })
  }

  const handleBase64 = ({ base64 }) => {
    setPostData({
      ...postData, selectedFile: base64
    })
  }

  return (
    <Paper className={classes.paper}>
      <form autoComplete='off' noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
        <Typography variant='h6'>Creating a Memories</Typography>
        {/* Creator */}
        <TextField
          name='creator'
          variant='outlined'
          label='Creator'
          fullWidth
          value={postData.creator}
          onChange={handlChange} />

        {/* Title */}
        <TextField
          name='title'
          variant='outlined'
          label='Title'
          fullWidth
          value={postData.title}
          onChange={handlChange} />

        {/* Message */}
        <TextField
          name='message'
          variant='outlined'
          label='Message'
          fullWidth
          value={postData.message}
          onChange={handlChange} />

        {/* Tags */}
        <TextField
          name='tags'
          variant='outlined'
          label='Tags'
          fullWidth
          value={postData.tags}
          onChange={handlChange} />

        <div className={classes.fileInput}>
          <FileBase
            type='file'
            multiple={false}
            onDone={handleBase64}
          />
        </div>

        <Button
          className={classes.buttonSubmit}
          variant='contained'
          color='primary'
          size='large'
          type='submit'
          fullWidth>Submit</Button>
        <Button
          className={classes.buttonSubmit}
          variant='contained'
          color='secondary'
          size='small'
          onClick={clear}
          fullWidth>Clear</Button>
      </form>
    </Paper>
  )
}

export default Form
